    public static Collection<? extends Katalog> getListData(){
        Katalog katalog = null;
        ArrayList<Katalog> list = new ArrayList<>();
        for (int i = 0; i <data.length; i++) {
            katalog = new Katalog();
            katalog.setGambar(data[i][0]);
            katalog.setJudul(data[i][1]);
            katalog.setIsi(data[i][2]);

            list.add(katalog);
        }

        return list;
    }
